import axios from 'axios'
import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import Loginpage from '../pages/login';
const Profile = ()=>{
    const [userdata, setuserdata] = useState('');
    const navigate = useNavigate();
    const getuserData = ()=>{
        axios.get("http://localhost:8585/users/singleuserlist").then((response)=>{
            setuserdata(response.data.message)
        })
    }
    useEffect(()=>{if(location.pathname === '/login'){
        localStorage.setItem('email',response.data.message.email)
                    localStorage.setItem('firstname',response.data.message.firstname)
                    localStorage.setItem('id',response.data.message._id)
                    getuserData();}
                //    navigate('/profile')
 
    })
    // const handleDelete = (userId)=>{
    //     axios.delete('http://localhost:8585/users/deleteuser/${userId}').then((response)=>{
    //         // console.log(response)
    //         getuserData();
    //     })
    // }
    
    return(
        <>
                { userdata && userdata.map((user)=>(
                    <ul>
                        <li>User id{user._id}</li>
                        <li>Firstname{user.firstname}</li>
                        <li>Lastname{user.lastname}</li>
                        <li>Email{user.email}</li>
                        <li>
                            {/* <input type='button' value="Delete" onClick={()=>{handleDelete(user._id)}}/> */}
                            <input type='button' value="Edit" onClick={()=>{navigate(`/edit/${user._id}`)}}/>
                            
                        </li>
                    </ul>
                ))}
            
        
        </>
    )
}
