import axios from 'axios'
import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
const Profile = ()=>{
    const [userdata, setprofiledata] = useState('');
    const navigate = useNavigate();
    const getalluserData = ()=>{
        axios.get("http://localhost:8585/users/userlist").then((response)=>{
            setprofliedata(response.data.message)
        })
    }
    useEffect(()=>{
        getalluserData();
    },[])
    const handleDelete = (userId)=>{
        axios.delete('http://localhost:8585/users/deleteuser/${userId}').then((response)=>{
            // console.log(response)
            getalluserData();
        })
    }
   
    return(
        <>
         <table border={1} width={800} align="center">
                <tr>
                    <th>User id </th><th>First name </th><th>Last name</th><th>Email</th><th>Action</th>
                </tr>
                { userdata && userdata.map((user)=>(
                    <tr>
                        <td>{user._id}</td>
                        <td>{user.firstname}</td>
                        <td>{user.lastname}</td>
                        <td>{user.email}</td>
                        <td>
                            <input type='button' value="Delete" onClick={()=>{handleDelete(user._id)}}/>
                            <input type='button' value="Edit" onClick={()=>{navigate(`/edit/${user._id}`)}}/>
                            
                        </td>
                    </tr>
                ))}
            
         </table>
        </>
    )
}
